import numpy as np
import matplotlib
import matplotlib.pyplot as plt
import scipy.interpolate as spi

#---------------------------------------------------------------------------------------------------
# Graphics construction.
#---------------------------------------------------------------------------------------------------

def simple_graphic_ys(ys, title = 'title'):
    """
    Draw simple graphic by only Y coordinates.

    Arguments:
        ys -- array of Y coordinates,
        title -- title.
    """

    simple_graphic(range(len(ys)), ys, title)

#---------------------------------------------------------------------------------------------------

def simple_graphic(xs, ys, title = 'title'):
    """
    Draw simple graphic.

    Arguments:
        xs -- array of X coordinates,
        ys -- array of Y coordinates,
        title -- title.
    """
    plt.figure(num=1, figsize=(10, 6))
    plt.title(title, size=14)
    plt.xlabel('x_label', size=14)
    plt.ylabel('y_label', size=14)

    # Draw.
    plt.plot(xs, ys, color='r', linestyle='-', label='graphic')

    # Legend.
    plt.legend(loc='lower left')

    # Save figure.
    plt.savefig('simple_graphic.png', format='png')

#---------------------------------------------------------------------------------------------------

def interpolation_test(xs, ys, title):
    """
    Draw simple graphic.

    Arguments:
        xs -- array of X coordinates,
        ys -- array of Y coordinates,
        title -- title.
    """

    plt.figure(num=1, figsize=(10, 6))
    plt.title(title, size=14)
    plt.xlabel('t (градусы Цельсия)', size=14)
    plt.ylabel('L_ev (МДж/кг)', size=14)

    # Draw.
    plt.plot(xs, [2.5] * len(xs), color='b', linestyle='-', label='постоянное значение')
    f = spi.interp1d(xs, ys, kind='nearest')
    plt.plot(range(0, 374), f(range(0, 374)), color='y', linestyle='-',
             label='кусочно-постоянная интерполяция')
    plt.plot(xs, ys, color='r', linestyle='-', marker='o', label='кусочно-линейная интерполяция')
    tck = spi.splrep(xs, ys, s=0)
    r = range(0, 375)
    nys = spi.splev(r, tck, der=0)
    plt.plot(r, nys, color='g', linestyle='-', label='использование гладкой интерполяции')

    # Legend.
    plt.legend(loc='lower left')

    # Save figure.
    plt.savefig('interpolation_test.png', format='png')

#---------------------------------------------------------------------------------------------------

def ice_grow_test(rect, trap):
    """
    Draw test for ice grow.

    Arguments:
        rect -- array of errors for rectangles method,
        trap -- array of errors for trapeziums method.
    """

    f = matplotlib.font_manager.FontProperties(size = 16)

    matplotlib.rc('xtick', labelsize = 16)
    matplotlib.rc('ytick', labelsize = 16)

    plt.figure(num=1, figsize=(10, 6))
    plt.xlabel('cell number', size = 20)
    plt.ylabel('area deviation (%)', size = 20)
    plt.grid(axis = 'both')

    # Draw.
    plt.plot(range(len(rect)), rect, color = 'black', linestyle = '-', label = 'rectangles method')
    plt.plot(range(len(trap)), trap, color = 'black', linestyle = '--', label = 'trapeziums method')

    # Legend.
    plt.legend(loc = 'lower right', prop = f)

    # Save figure.
    plt.savefig('ice_grow_test.png', format = 'png')

